import React, { Component } from 'react';

export default class extends Component {
  static async getInitialProps({ query }) {
  let idChannel = query.id;

  let reqChannel = await fetch
  (`https://api.audioboom.com/channels/${idChannel}`)
  let dataChannel = await reqChannel.json()
    let channel = dataChannel.body.channel;

  let reqAudio = await fetch
  (`https://api.audioboom.com/channels/${idChannel}/audio_clips`)
  let dataAudios = await reqAudio.json();
  let audioClips = dataAudios.body.audio_clips

  let reqSeries = await fetch
  (`https://api.audioboom.com/channels/${idChannel}/child_channels`)
  let dataSeries = await reqSeries.json();
  let series = dataSeries.body.channels

  return { channel, audioClips, series }
  }
  render() {
    const { channel, audioClips, series } = this.props;
    return <div>
      <header>Podcast </header>
      <h1>
       {channel.title}
      </h1>
      {
        audioClips.map((clip => (
          <div>
            {clip.title}
          </div>
        ))) 
      }
       {
        series.map((serie => (
          <div>
            {serie.title}
          </div>
        ))) 
      }
      <style jsx> {`
        header {
          color: white;
          background: #8756ca;
          padding: 15px;
          text-align: center;
        }
        h1 {
          padding: 15px,
        }
        .channels {
          display: grid;
          grid-gap: 15px;
          padding: 15px;
          grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
        }
        a.channel {
          display: block;
          margin-bottom: 0.5em;
          color: #333;
          text-decoration: none;
        }
        .channel img {
          border-radius: 3px;
          box-shadow: 0px 2px 6px rgba(0,0,0,0.15);
          width: 100%;
        }
        h2 {
          padding: 5px;
          font-size: 0.9em;
          font-weight: 600;
          margin: 0;
          text-align: center;
          color: #8756ca;
        }
      `}
      </style>

      <style jsx global> {`
        body {
          margin: 0;
          background: lightcyan;
          font-family: system-ui;
        }
      `}
      </style>
    </div>
  }
};
