import React, { Component } from 'react';
import 'isomorphic-fetch';

export default class extends Component {

  static async getServerSideProps() {
    let req = await fetch
    ('https://api.audioboom.com/channels/recommended')
    let { body: channels } = await req.json()
    return { channels }
  }

  render() {
    const { channels } = this.props;
    return <div>
      <header>Podcasts</header>
      {
        channels.map(chanel => <div>{chanel.title}</div>)
      }
      <style jsx> {`
        header {
          color: white;
          background: #8756ca;
          padding: 15px;
          text-align: center;
        }
      `}
      </style>

      <style jsx global> {`
        body {
          margin: 0;
          background: white;
          font-family: system-ui;
        }
      `}
      </style>
    </div>
  }
};
